(window.webpackJsonp=window.webpackJsonp||[]).push([[162],{712:function(a,t,i){"use strict";i.r(t);var p=i(19),r=Object(p.a)({},(function(){var a=this,t=a.$createElement,i=a._self._c||t;return i("ContentSlotsDistributor",{attrs:{"slot-key":a.$parent.slotKey}},[i("p",[a._v("JavaScript 从互联网萌芽时期开始就一直是实现交互体验的基本技术。虽然最初被用来实现闪烁的鼠标轨迹和烦人的弹出消息框，但在大约二十年以后，它在技术和功能方面都得到了很大的提升，几乎没有人再质疑它在互联网中的重要地位。")]),a._v(" "),i("p",[a._v("但是，作为一门编程语言，JavaScript 一直为人诟病，部分原因是其历史沿革，更重要的原因则是其设计理念。因为 JavaScript 这个名字，Brendan Eich 曾戏称它为“傻小弟”（ 相对于成熟的 Java 而 言 ）。 实际上，这个名字完全是政治和市场考量下的产物。两门语言之间千差万别 ，“JavaScript”之于“Java”就如同“Carnival”（嘉年华）之于“Car”（ 汽 车 ）一样，两者之间并无半点关系。")]),a._v(" "),i("p",[a._v("JavaScript 在概念和语法风格上借鉴了其他编程语言，包括 C 风格的过程式编程和隐晦的Scheme/Lisp 风格的函数式编程，这使得它能为不同背景的开发人员所接受，包括那些没有多少编程经验的人。用 JavaScript 编写一个“Hello World”程序非常简单。")]),a._v(" "),i("p",[a._v("JavaScript 可能是最容易上手的编程语言之一，但它的一些奇特之处使得它不像其他语言那样容易完全掌握。要想用 C 或 者 C++ 开发一个完整的应用程序，开发者需要对该门语言有相当深入的了解。然而对于 JavaScript，即使我们用它开发了一个完整的系统也不见得就能深入理解它。")]),a._v(" "),i("p",[a._v("这门语言中有些复杂的概念隐藏得很深，却常常以一种看似简单的形式呈现。例如，将函数作为回调函数传递，这让 JavaScript 开 发人员往往满足于使用这些现成便利的机制，而不愿去探究其中的原理。")]),a._v(" "),i("BookShelf",{attrs:{album:"https://cdn.staticaly.com/gh/jonsam-ng/image-hosting@master/oxygen-space/image.549txue6kmc0.webp",pages:213,link:"https://www.aliyundrive.com/s/PRDXLJvEUM2",douban:"https://book.douban.com/subject/26351021/",author:"[美] Kyle Simpson",publisher:"人民邮电出版社",intro:"本书介绍了该系列的两个主题：“作用域和闭包”以及“this和对象原型”。掌握了这些知识之后，无论什么技术、框架和流行词语，你都能轻松理解。",lang:"中文"}}),a._v(" "),i("p",[a._v("JavaScript语言有很多复杂的概念，但却用简单的方式体现出来（比如回调函数），因此，JavaScript开发者无需理解语言内部的原理，就能编写出功能全面的程序；就像收音机一样，你无需理解里面的管子和线圈都是做什么用的，只要会操作收音机上的按键，就可以收听你喜欢的节目。然而，JavaScript的这些复杂精妙的概念才是语言的精髓，即使是经验丰富的JavaScript开发者，如果没有认真学习也无法真正理解语言本身的特性。正是因为绝大多数人不求甚解，一遇到出乎意料的行为就认为是语言本身有缺陷，进而把相关的特性加入黑名单，久而久之就排除了这门语言的多样性，人为地使它变得不完整、不安全。")]),a._v(" "),i("p",[a._v("“你不知道的JavaScript”系列就是要让不求甚解的JavaScript开发者迎难而上，深入语言内部，弄清楚JavaScript每一个零部件的用途。本书介绍了该系列的两个主题：“作用域和闭包”以及“this和对象原型”。掌握了这些知识之后，无论什么技术、框架和流行词语，你都能轻松理解。")]),a._v(" "),i("BookShelf",{attrs:{album:"https://cdn.staticaly.com/gh/jonsam-ng/image-hosting@master/oxygen-space/image.534dmb3d6b80.webp",pages:382,link:"https://www.aliyundrive.com/s/vNmLQX5tSwH",douban:"https://book.douban.com/subject/26854244/",author:"[美] Kyle Simpson",publisher:"人民邮电出版社",intro:"本套书直面当前JavaScript开发人员不求甚解的大趋势，深入理解语言内部的机制，全面介绍了JavaScript中常被人误解和忽视的重要知识点。本书是其中卷，主要介绍了类型、语法、异步和性能。",lang:"中文"}}),a._v(" "),i("p",[a._v("JavaScript 是一门简单易用的语言，应用广泛，同时它的语言机制又十分复杂和微妙， 即使经验丰富的开发人员也需要用心学习才能真正掌握。JavaScript 的矛盾之处就在于此，它的阿喀琉斯之踵正是本书要解决的问题。因为无需深入理解就能用它来编程，所以人们常常放松对它的学习。")]),a._v(" "),i("p",[a._v("JavaScript这门语言简单易用，很容易上手，但其语言机制复杂微妙，即使是经验丰富的JavaScript开发人员，如果没有认真学习的话也无法真正理解。本套书直面当前JavaScript开发人员不求甚解的大趋势，深入理解语言内部的机制，全面介绍了JavaScript中常被人误解和忽视的重要知识点。本书是其中卷，主要介绍了类型、语法、异步和性能。")]),a._v(" "),i("BookShelf",{attrs:{album:"https://cdn.staticaly.com/gh/jonsam-ng/image-hosting@master/oxygen-space/image.1dafp8arkpj4.webp",pages:290,link:"https://www.aliyundrive.com/s/x6AG99B4v5F",douban:"",author:"[美] Kyle Simpson",publisher:"人民邮电出版社",intro:"",lang:"中文"}})],1)}),[],!1,null,null,null);t.default=r.exports}}]);