(window.webpackJsonp=window.webpackJsonp||[]).push([[53],{606:function(t,e,n){"use strict";n.r(e);var o=n(19),a=Object(o.a)({},(function(){var t=this,e=t.$createElement,n=t._self._c||e;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("p",[t._v("仁波切把将佛法弘传到西方，当作是他毕生的志业，因此写出《西藏生死书》，提供修炼的基础。这项修炼可使人们了解佛法，并实际与日常生活相结合，因此能裨益他人和全世界。")]),t._v(" "),n("p",[t._v("索甲仁波切出生于西藏，由二十世纪最受敬重的精神导师蒋扬钦哲仁波切(Jamyang Khyentse Chokyi Lodro)养育长大。自幼年起，他就与上师培养出温馨亲密的关系，并沉浸在智慧、慈悲和恭敬心的氛围之中。这使他对佛法精髓有了深刻的了解，以此体验和身为转世喇嘛所受的传统训练，为他日后担任精神导师的任务，打下厚实的基础。")]),t._v(" "),n("p",[t._v("蒋扬钦哲仁波切圆寂之后，索甲仁波切继续跟随二位主要上师敦珠仁波切和顶果钦哲仁波切修法，启发他在西方的传法志业。1971年，他前往英国剑桥大学研读比较宗教学。1974年开始弘法，因为求法者热烈渴求，他又继续至欧洲、东欧、美国、澳洲和亚洲等地传法。")]),t._v(" "),n("p",[t._v("很少上师能有索甲仁波切天赋的沟通能力，而二十余年在西方的生活和受教，使他深刻了解西方人的心灵。他以亲切、幽默和讲法清晰闻名；且能够跨越宗教、文化和心理学的藩篱，揭示了佛法的精髓。他以日常生活令人惊妙的例子，分享他个人的经验，因而能直接与学生的经验相契合，进而激起对佛法的鲜活感受和韵味。")]),t._v(" "),n("p",[t._v("索甲仁波切传承于伟大的大圆满教法，他具有这个传承的明显特质：充沛的活力、广阔的心灵和直指人心。")]),t._v(" "),n("BookShelf",{attrs:{album:"https://cdn.staticaly.com/gh/jonsam-ng/image-hosting@master/oxygen-space/image.5egbbz4l9aw0.webp",pages:419,author:"索甲仁波切",intro:"仁波切把将佛法弘传到西方，当作是他毕生的志业，因此写出《西藏生死书》，提供修炼的基础。这项修炼可使人们了解佛法，并实际与日常生活相结合，因此能裨益他人和全世界。",publisher:"浙江大学出版社",lang:"中文",link:"/books/西藏生死书.epub",douban:"5990251"}})],1)}),[],!1,null,null,null);e.default=a.exports}}]);